# Deploy JobMatch Backend to Google Cloud Run
# Usage: .\scripts\deploy-to-cloud-run.ps1

param(
    [string]$ServiceName = "jobmatch-backend",
    [string]$Region = "us-central1",
    [string]$ImageTag = "latest",
    [switch]$SkipBuild = $false
)

$ErrorActionPreference = "Stop"

# Colors
function Write-Info($message) { Write-Host $message -ForegroundColor Cyan }
function Write-Success($message) { Write-Host $message -ForegroundColor Green }
function Write-Warning($message) { Write-Host $message -ForegroundColor Yellow }
function Write-Error($message) { Write-Host $message -ForegroundColor Red }

# Get project ID
$ProjectId = gcloud config get-value project 2>$null
if (-not $ProjectId) {
    Write-Error "Error: No GCP project configured. Run: gcloud config set project PROJECT_ID"
    exit 1
}

$ImageName = "gcr.io/$ProjectId/$ServiceName`:$ImageTag"

Write-Info "🚀 Deploying JobMatch Backend to Google Cloud Run"
Write-Info "Project: $ProjectId"
Write-Info "Service: $ServiceName"
Write-Info "Region: $Region"
Write-Info "Image: $ImageName"
Write-Host ""

# Check prerequisites
Write-Info "📋 Checking prerequisites..."

try {
    gcloud --version | Out-Null
    Write-Success "✅ gcloud CLI found"
} catch {
    Write-Error "❌ gcloud CLI not found. Install from: https://cloud.google.com/sdk/docs/install"
    exit 1
}

try {
    docker --version | Out-Null
    Write-Success "✅ Docker found"
} catch {
    Write-Error "❌ Docker not found. Install from: https://www.docker.com/get-started"
    exit 1
}

# Enable APIs
Write-Info "🔧 Enabling required APIs..."
gcloud services enable cloudbuild.googleapis.com run.googleapis.com containerregistry.googleapis.com --quiet 2>$null
Write-Success "✅ APIs enabled"

# Authenticate Docker
Write-Info "🔐 Authenticating Docker with GCR..."
gcloud auth configure-docker --quiet 2>$null
Write-Success "✅ Docker authenticated"

# Build image
if (-not $SkipBuild) {
    Write-Info "🔨 Building Docker image..."
    Write-Warning "This may take several minutes..."
    
    docker build -f Dockerfile.cloudrun -t $ImageName .
    
    if ($LASTEXITCODE -ne 0) {
        Write-Error "❌ Docker build failed"
        exit 1
    }
    Write-Success "✅ Image built successfully"
} else {
    Write-Warning "⏭️  Skipping build (using existing image)"
}

# Push image
Write-Info "📤 Pushing image to Google Container Registry..."
docker push $ImageName

if ($LASTEXITCODE -ne 0) {
    Write-Error "❌ Failed to push image"
    exit 1
}
Write-Success "✅ Image pushed successfully"

# Deploy to Cloud Run
Write-Info "🚀 Deploying to Cloud Run..."
Write-Warning "This may take a few minutes..."

gcloud run deploy $ServiceName `
    --image $ImageName `
    --platform managed `
    --region $Region `
    --port 8080 `
    --memory 512Mi `
    --cpu 1 `
    --min-instances 0 `
    --max-instances 10 `
    --timeout 300 `
    --allow-unauthenticated `
    --quiet

if ($LASTEXITCODE -ne 0) {
    Write-Error "❌ Deployment failed"
    exit 1
}

# Get service URL
$ServiceUrl = gcloud run services describe $ServiceName `
    --region $Region `
    --format="value(status.url)"

Write-Host ""
